{
  "lessons": [
    {
      "description": "The fundamental equation relating a dependent variable to a set of explanatory variables and an error term.",
      "title": "The Linear Regression Equation"
    },
    {
      "description": "The organization of independent variables into a mathematical matrix format for OLS estimation.",
      "title": "The Design Matrix"
    },
    {
      "description": "Representing linear regression as a problem of vectors in an N-dimensional Euclidean space.",
      "title": "Vector Space Representation"
    },
    {
      "description": "The subspace spanned by the columns of the design matrix where the predicted values must lie.",
      "title": "The Column Space of X"
    },
    {
      "description": "Finding the point in the column space that minimizes the distance to the observed data vector.",
      "title": "Orthogonal Projection of Y"
    },
    {
      "description": "The difference between observed values and the projected values on the column space.",
      "title": "The Residual Vector"
    },
    {
      "description": "The geometric requirement that the residual vector is perpendicular to every column in the design matrix.",
      "title": "Orthogonality of Residuals"
    },
    {
      "description": "The system of linear equations derived from setting the gradient of the sum of squared residuals to zero.",
      "title": "The Normal Equations"
    },
    {
      "description": "The linear operator that maps the observed vector Y to the predicted vector Y-hat.",
      "title": "The Hat Matrix"
    },
    {
      "description": "The property where applying the Hat matrix multiple times yields the same result as a single application.",
      "title": "Idempotency of the Hat Matrix"
    },
    {
      "description": "The matrix that transforms the observed data vector into the vector of OLS residuals.",
      "title": "The Annihilator Matrix"
    },
    {
      "description": "The requirement that the model equation is linear with respect to its coefficients.",
      "title": "Linearity in Parameters"
    },
    {
      "description": "The assumption that data points are drawn independently from the same underlying distribution.",
      "title": "Random Sampling"
    },
    {
      "description": "The requirement that no independent variable is a perfect linear combination of the others.",
      "title": "The Full Rank Condition"
    },
    {
      "description": "The assumption that the error term has an expected value of zero given any values of the independent variables.",
      "title": "Zero Conditional Mean"
    },
    {
      "description": "The state where the independent variables are uncorrelated with the error term.",
      "title": "Exogeneity"
    },
    {
      "description": "The assumption that the variance of the error term is constant across all observations.",
      "title": "Homoskedasticity"
    },
    {
      "description": "The assumption that error terms for different observations are uncorrelated with each other.",
      "title": "Non-Autocorrelation"
    },
    {
      "description": "The combination of homoskedasticity and non-autocorrelation into a single error covariance structure.",
      "title": "Spherical Errors"
    },
    {
      "description": "The condition where a unique set of parameters can be determined from the population distribution.",
      "title": "Identification in Linear Models"
    },
    {
      "description": "The algebraic necessity of having at least as many observations as parameters for identification.",
      "title": "Rank Condition for Identification"
    },
    {
      "description": "The closed-form algebraic expression used to calculate OLS coefficient estimates.",
      "title": "The OLS Estimator Formula"
    },
    {
      "description": "Demonstrating that the OLS estimator is a linear function of the dependent variable vector.",
      "title": "Linearity of the OLS Estimator"
    },
    {
      "description": "Mathematical proof that the expected value of the OLS estimator equals the population parameters.",
      "title": "Unbiasedness of OLS"
    },
    {
      "description": "The matrix representing the variances and covariances of the estimated coefficients.",
      "title": "The Beta Variance-Covariance Matrix"
    },
    {
      "description": "Defining the criteria for an estimator to be the Best Linear Unbiased Estimator.",
      "title": "Defining BLUE"
    },
    {
      "description": "The theorem stating that OLS is the most efficient linear unbiased estimator under classical assumptions.",
      "title": "The Gauss-Markov Theorem"
    },
    {
      "description": "The concept of minimum variance among all possible linear unbiased estimators.",
      "title": "Efficiency of OLS"
    },
    {
      "description": "The assumption that error terms follow a normal distribution for the purpose of exact inference.",
      "title": "Normality of Errors"
    },
    {
      "description": "The probability distribution of the OLS estimates across repeated samples.",
      "title": "Sampling Distribution of Estimates"
    },
    {
      "description": "Calculating the unbiased estimate of the error variance from the sum of squared residuals.",
      "title": "Estimating Sigma-Squared"
    },
    {
      "description": "The estimated standard deviation of a coefficient's sampling distribution.",
      "title": "Standard Error of Beta"
    },
    {
      "description": "The ratio of an estimated coefficient to its standard error used for hypothesis testing.",
      "title": "The T-Statistic"
    },
    {
      "description": "The procedure for evaluating if a specific independent variable has a non-zero effect.",
      "title": "Testing Coefficient Significance"
    },
    {
      "description": "The probability of observing a test statistic as extreme as the one calculated, assuming the null is true.",
      "title": "P-Values in Regression"
    },
    {
      "description": "The range of values within which the true parameter is expected to fall with a specific probability.",
      "title": "Confidence Interval Construction"
    },
    {
      "description": "The statistical test used to evaluate multiple linear restrictions on coefficients simultaneously.",
      "title": "The F-Test"
    },
    {
      "description": "The model version where all parameters are free to be estimated without constraints.",
      "title": "The Unrestricted Model"
    },
    {
      "description": "The model version where certain linear constraints are imposed on the coefficients.",
      "title": "The Restricted Model"
    },
    {
      "description": "The proportion of total variance in the dependent variable explained by the independent variables.",
      "title": "Coefficient of Determination"
    },
    {
      "description": "A modified version of R-squared that penalizes the inclusion of unnecessary independent variables.",
      "title": "Adjusted R-Squared"
    },
    {
      "description": "Using plots of residuals against fitted values to identify model specification issues.",
      "title": "Residual Analysis"
    },
    {
      "description": "The impact of non-constant error variance on the efficiency and validity of OLS inference.",
      "title": "Consequences of Heteroskedasticity"
    },
    {
      "description": "A formal statistical test for heteroskedasticity based on squared residuals.",
      "title": "The Breusch-Pagan Test"
    },
    {
      "description": "A test specifically designed to detect first-order serial correlation in regression residuals.",
      "title": "The Durbin-Watson Statistic"
    },
    {
      "description": "The situation where independent variables are highly correlated with each other.",
      "title": "Multicollinearity"
    },
    {
      "description": "A diagnostic tool that measures how much the variance of an estimated coefficient is increased by collinearity.",
      "title": "Variance Inflation Factor"
    },
    {
      "description": "The bias that occurs when a relevant variable is left out of the regression model.",
      "title": "Omitted Variable Bias"
    },
    {
      "description": "The mathematical derivation showing how the omission of a variable affects the remaining coefficient estimates.",
      "title": "The Omitted Variable Bias Formula"
    }
  ]
}

