    {
      "description": "Organizing regressors into an n×k matrix of explanatory variables.",
      "title": "OLS Geometry: Design Matrix"
    },
    {
      "description": "The subspace spanned by the regressors where fitted values must lie.",
      "title": "OLS Geometry: Column Space"
    },
    {
      "description": "Projecting the outcome vector onto the column space to obtain fitted values.",
      "title": "OLS Geometry: Orthogonal Projection"
    },
    {
      "description": "The orthogonal complement between observed and predicted values.",
      "title": "OLS Geometry: Residual Vector"
    },
    {
      "description": "Total sum of squares equals explained plus residual sum of squares.",
      "title": "OLS Geometry: Pythagorean Decomposition"
    },
    {
      "description": "The population model is linear in the coefficients.",
      "title": "OLS Assumption 1: Linearity in Parameters"
    },
    {
      "description": "Data are a random sample from the population of interest.",
      "title": "OLS Assumption 2: Random Sampling"
    },
    {
      "description": "No exact linear relationships among regressors in the sample.",
      "title": "OLS Assumption 3: No Perfect Multicollinearity"
    },
    {
      "description": "Error terms have expected value zero given any values of regressors.",
      "title": "OLS Assumption 4: Zero Conditional Mean of Errors"
    },
    {
      "description": "Error variance is constant across all observations.",
      "title": "OLS Assumption 5: Homoskedasticity"
    },
    {
      "description": "Error terms are uncorrelated across observations.",
      "title": "OLS Assumption 6: No Autocorrelation"
    },
    {
      "description": "A parameter is identified when different values produce different distributions of the data.",
