{
  "lessons": [
    {
      "description": "Vector of observed outcomes Y in n observations.",
      "title": "Dependent Variable Vector Y"
    },
    {
      "description": "Matrix X with n rows of observations and k+1 columns including intercept.",
      "title": "Regressor Matrix X"
    },
    {
      "description": "True parameter vector β of dimension k+1.",
      "title": "Parameter Vector β"
    },
    {
      "description": "Error term vector u with E(u)=0.",
      "title": "Error Vector u"
    },
    {
      "description": "Linear model Y = Xβ + u.",
      "title": "Linear Population Model"
    },
    {
      "description": "Sample analogue y = Xβ + ε.",
      "title": "Linear Sample Model"
    },
    {
      "description": "Fitted values vector ŷ = X(X'X)^{-1}X'y.",
      "title": "OLS Fitted Values ŷ"
    },
    {
      "description": "Residuals vector e = y - ŷ.",
      "title": "OLS Residuals e"
    },
    {
      "description": "Column space of X, span of regressor columns.",
      "title": "Column Space of X"
    },
    {
      "description": "Orthogonal projection of y onto column space of X.",
      "title": "Orthogonal Projection onto X"
    },
    {
      "description": "Projection matrix P = X(X'X)^{-1}X'.",
      "title": "Hat Matrix P"
    },
    {
      "description": "ŷ = P y, residuals e = (I - P) y.",
      "title": "Projection and Residual Formulas"
    },
    {
      "description": "Residuals e perpendicular to every column of X.",
      "title": "Orthogonality of Residuals"
    },
    {
      "description": "Trace(P) = k+1 degrees of freedom.",
      "title": "Degrees of Freedom in Projection"
    },
    {
      "description": "Sum of squared residuals ||e||^2 minimized by OLS.",
      "title": "SSR Minimization in Geometry"
    },
    {
      "description": "||y||^2 = ||ŷ||^2 + ||e||^2 Pythagoras theorem.",
      "title": "Pythagorean Identity in OLS"
    },
    {
      "description": "Linearity in parameters Y = Xβ + u.",
      "title": "Linearity Assumption MLR.1"
    },
    {
      "description": "Random sample of n independent observations.",
      "title": "Random Sampling Assumption MLR.2"
    },
    {
      "description": "No perfect collinearity among X columns.",
      "title": "No Perfect Collinearity MLR.3"
    },
    {
      "description": "E(ε_i | X) = 0 for all i.",
      "title": "Zero Conditional Mean MLR.4"
    },
    {
      "description": "Var(ε_i | X) = σ² for all i.",
      "title": "Homoskedasticity MLR.5"
    },
    {
      "description": "ε_i | X ~ N(0, σ²) independent.",
      "title": "Normality Assumption MLR.6"
    },
    {
      "description": "OLS estimator β̂ = (X'X)^{-1}X'y.",
      "title": "OLS Estimator Formula"
    },
    {
      "description": "E(β̂) = β under MLR.4.",
      "title": "Unbiasedness of OLS"
    },
    {
      "description": "Var(β̂) = σ² (X'X)^{-1} under MLR.1-5.",
      "title": "Variance-Covariance Matrix of OLS"
    },
    {
      "description": "Gauss-Markov assumptions MLR.1 to MLR.5.",
      "title": "Gauss-Markov Assumptions"
    },
    {
      "description": "OLS is BLUE under Gauss-Markov assumptions.",
      "title": "Gauss-Markov Theorem"
    },
    {
      "description": "Lowest variance among linear unbiased estimators.",
      "title": "BLUE Property"
    },
    {
      "description": "Exogeneity E(ε|X)=0 ensures identification.",
      "title": "Exogeneity for Identification"
    },
    {
      "description": "Full rank of E(X'X/n) for identification.",
      "title": "Rank Condition for Identification"
    },
    {
      "description": "Unique β consistent with population moments.",
      "title": "Identification of Parameters"
    },
    {
      "description": "H_0: β_j = β_j^0 vs H_a: β_j ≠ β_j^0.",
      "title": "t-Test Null Hypothesis"
    },
    {
      "description": "t-statistic t = (β̂_j - β_j^0) / se(β̂_j).",
      "title": "t-Statistic Formula"
    },
    {
      "description": "t ~ t_{n-k-1} under H_0 and MLR.1-6.",
      "title": "t-Distribution under Null"
    },
    {
      "description": "Reject H_0 if |t| > critical value.",
      "title": "t-Test Decision Rule"
    },
    {
      "description": "95% confidence interval β̂_j ± t^* se(β̂_j).",
      "title": "Confidence Interval for β_j"
