    {
      "description": "Residual sum of squares SSR.",
      "title": "Residual Plots Overview"
    },
    {
      "description": "Plot residuals vs fitted values.",
      "title": "Residuals vs Fitted Plot"
    },
    {
      "description": "Histogram of standardized residuals.",
      "title": "Residuals Histogram"
