{
  "lessons": [
    {
      "description": "Fluctuations in aggregate economic activity around a long-term growth trend.",
      "title": "What is a Business Cycle?"
    },
    {
      "description": "The period from a trough to a peak characterized by increasing economic activity.",
      "title": "Expansion Phase"
    },
    {
      "description": "The highest point of economic activity before a contraction begins.",
      "title": "The Economic Peak"
    },
    {
      "description": "The period from a peak to a trough characterized by decreasing economic activity.",
      "title": "Contraction Phase"
    },
    {
      "description": "The lowest point of economic activity before an expansion begins.",
      "title": "The Economic Trough"
    },
    {
      "description": "The long-run path of an economy independent of short-term fluctuations.",
      "title": "The Trend Component"
    },
    {
      "description": "Short-term deviations of actual output from the long-run trend.",
      "title": "The Cyclical Component"
    },
    {
      "description": "Empirical regularities observed in macroeconomic data across different time periods and countries.",
      "title": "Identifying Stylized Facts"
    },
    {
      "description": "The tendency for positive or negative shocks to output to last for several periods.",
      "title": "Cycle Persistence"
    },
    {
      "description": "Measurement of the magnitude of fluctuations in economic variables over time.",
      "title": "Volatility in Cycles"
    },
    {
      "description": "The tendency for multiple macroeconomic variables to move together over the business cycle.",
      "title": "Concept of Co-movement"
    },
    {
      "description": "Economic variables that move in the same direction as aggregate economic activity.",
      "title": "Procyclical Variables"
    },
    {
      "description": "Economic variables that move in the opposite direction of aggregate economic activity.",
      "title": "Countercyclical Variables"
    },
    {
      "description": "Economic variables with no consistent correlation to the overall business cycle.",
      "title": "Acyclical Variables"
    },
    {
      "description": "Variables that typically change direction before the overall economy does.",
      "title": "Leading Indicators"
    },
    {
      "description": "Variables that typically change direction after the overall economy has already shifted.",
      "title": "Lagging Indicators"
    },
    {
      "description": "Variables that move simultaneously with the overall level of economic activity.",
      "title": "Coincident Indicators"
    },
    {
      "description": "The observation that consumption fluctuates less than total output over the cycle.",
      "title": "Consumption Volatility"
    },
    {
      "description": "The observation that investment fluctuates significantly more than total output.",
      "title": "Investment Volatility"
    },
    {
      "description": "The procyclical nature of the total number of people working.",
      "title": "Employment Co-movement"
    },
    {
      "description": "The countercyclical nature of the jobless rate relative to GDP growth.",
      "title": "Unemployment Cyclicality"
    },
    {
      "description": "The historical correlation between inflation-adjusted wages and output levels.",
      "title": "Real Wage Cyclicality"
    },
    {
      "description": "How equity price movements often precede changes in the broader business cycle.",
      "title": "Stock Market Lead"
    },
    {
      "description": "The relationship between interest rate spreads and future economic activity levels.",
      "title": "Yield Curve Lead"
    },
    {
      "description": "Differences in the duration or intensity between expansion and contraction periods.",
      "title": "Cycle Asymmetry"
    }
  ]
}

