{
  "lessons": [
    {
      "description": "Propiedad intrínseca de ciertos núcleos atómicos que genera un pequeño momento magnético.",
      "title": "El espín nuclear"
    },
    {
      "description": "Alineación de los espines nucleares a favor o en contra de un campo magnético aplicado.",
      "title": "Efecto del campo magnético externo"
    },
    {
      "description": "La cantidad exacta de energía de radiofrecuencia necesaria para invertir el estado de espín de un núcleo.",
      "title": "Frecuencia de resonancia"
    },
    {
      "description": "Técnica enfocada exclusivamente en la detección de átomos de hidrógeno dentro de una molécula.",
      "title": "RMN de protón (1H)"
    },
    {
      "description": "Detección del isótopo carbono-13 para analizar directamente el esqueleto carbonado de una sustancia.",
      "title": "RMN de carbono-13 (13C)"
    },
    {
      "description": "Líquidos modificados isotópicamente sin protones detectables que se usan para preparar muestras en el espectrómetro.",
      "title": "Disolventes deuterados"
    },
    {
      "description": "Compuesto estándar que define el punto cero absoluto en el eje horizontal de un espectro.",
      "title": "Tetrametilsilano (TMS)"
    },
    {
      "description": "Unidad de medida estandarizada que permite comparar espectros independientemente de la fuerza del imán utilizado.",
      "title": "La escala de partes por millón (ppm)"
    },
    {
      "description": "La posición específica de una señal en el espectro dictada por el entorno electrónico local del núcleo.",
      "title": "Desplazamiento químico"
    },
    {
      "description": "Protección del núcleo generada por una alta densidad electrónica que desplaza su señal hacia la derecha del espectro.",
      "title": "Apantallamiento magnético"
    },
    {
      "description": "Pérdida de densidad electrónica alrededor del núcleo que desplaza su señal hacia la izquierda del espectro.",
      "title": "Desapantallamiento magnético"
    },
    {
      "description": "Atracción de electrones por átomos cercanos ricos en electrones que genera desapantallamiento en los protones adyacentes.",
      "title": "Efecto de la electronegatividad"
    },
    {
      "description": "Generación de campos magnéticos inducidos localmente por el movimiento de electrones pi en enlaces múltiples.",
      "title": "Anisotropía magnética"
    },
    {
      "description": "El rango típico de señales de resonancia para protones unidos a carbonos con hibridación sp3.",
      "title": "Señales de alcanos"
    },
    {
      "description": "El rango característico de resonancia para protones ubicados directamente sobre dobles enlaces carbono-carbono.",
      "title": "Señales de alquenos"
    },
    {
      "description": "Señales fuertemente desapantalladas que aparecen en un rango alto debido a la corriente de anillo.",
      "title": "Señales de anillos aromáticos"
    },
    {
      "description": "Señales en posiciones extremas del espectro causadas por el efecto combinado del doble enlace y el oxígeno.",
      "title": "Señales de aldehídos"
    },
    {
      "description": "El proceso matemático de calcular el área bajo la curva de cada pico en un espectro de RMN.",
      "title": "Integración de señales"
    },
    {
      "description": "Uso de los valores de integración para deducir la cantidad relativa de hidrógenos que originan cada señal.",
      "title": "Cálculo de la proporción de protones"
    },
    {
      "description": "Hidrógenos que comparten exactamente el mismo entorno químico y espacial, produciendo una única señal combinada.",
      "title": "Protones equivalentes"
    },
    {
      "description": "Hidrógenos ubicados en entornos químicos distintos dentro de la molécula, generando señales separadas en el espectro.",
      "title": "Protones no equivalentes"
    },
    {
      "description": "La interacción magnética mutua entre núcleos cercanos transmitida a través de los electrones de los enlaces químicos.",
      "title": "Acoplamiento espín-espín"
    },
    {
      "description": "Fórmula matemática básica utilizada para predecir la cantidad de picos en los que se dividirá una señal.",
      "title": "La regla n+1"
    },
    {
      "description": "Un pico único en el espectro generado por protones que no tienen hidrógenos acoplables en los átomos adyacentes.",
      "title": "La señal singulete"
    },
    {
      "description": "Una señal dividida exactamente en dos picos debido a la interacción magnética con un solo protón vecino.",
      "title": "La señal doblete"
    },
    {
      "description": "Una señal dividida en tres picos causada por el acoplamiento magnético con dos protones adyacentes equivalentes.",
      "title": "La señal triplete"
    },
    {
      "description": "Una señal dividida en cuatro picos originada por la interacción directa con un grupo de tres protones vecinos.",
      "title": "La señal cuartete"
    },
    {
      "description": "La distancia exacta medida en hercios entre los picos individuales que forman una señal múltiple.",
      "title": "La constante de acoplamiento (J)"
    },
    {
      "description": "Representación gráfica utilizada para desglosar visualmente patrones de acoplamiento complejos paso a paso.",
      "title": "Diagramas de árbol"
    },
    {
      "description": "Obtención de espectros que emplean dos ejes de frecuencia separados para revelar correlaciones ocultas entre núcleos.",
      "title": "Fundamentos de la RMN 2D"
    },
    {
      "description": "Técnica bidimensional homonuclear diseñada específicamente para identificar qué protones están acoplados entre sí.",
      "title": "Espectroscopía COSY"
    },
    {
      "description": "Uso de la diagonal principal y los picos cruzados para trazar el mapa de conexiones entre hidrógenos vecinos.",
      "title": "Lectura de picos cruzados"
    },
    {
      "description": "Técnica heteronuclear que correlaciona cada protón directamente con el átomo de carbono específico al que está unido.",
      "title": "Espectroscopía HSQC"
    },
    {
      "description": "Técnica bidimensional que detecta correlaciones a larga distancia entre protones y carbonos separados por múltiples enlaces.",
      "title": "Espectroscopía HMBC"
    },
    {
      "description": "Transferencia directa de magnetización a través del espacio vacío entre núcleos que están físicamente cerca.",
      "title": "El efecto Overhauser nuclear (NOE)"
    },
    {
      "description": "Técnica bidimensional que aprovecha el efecto NOE para determinar la proximidad tridimensional de los átomos.",
      "title": "Espectroscopía NOESY"
    },
    {
      "description": "Uso de la fórmula molecular para calcular el número de anillos y enlaces múltiples presentes en un compuesto.",
      "title": "Cálculo de grados de insaturación"
    },
    {
      "description": "Asignación de subestructuras químicas específicas a partir de señales aisladas analizadas en el espectro.",
      "title": "Identificación de fragmentos estructurales"
    },
    {
      "description": "El proceso lógico de conectar los fragmentos químicos identificados para formar una molécula completa y coherente.",
      "title": "Ensamblaje de la estructura molecular"
    },
    {
      "description": "Comprobación final de la molécula propuesta simulando teóricamente sus propiedades magnéticas esperadas.",
      "title": "Verificación del modelo propuesto"
    }
  ]
}

